import React, { useState } from "react";
import dash from "./img/dash.svg";
import "./SideNavbar.css";
import bank from "./img/bank-building@2x 1.svg";
import wallet from "./img/wallet.svg";
import exchange from "./img/exchange.svg";
import learn from "./img/learn.svg";
import blog from "./img/blog.svg";
import profile from "./img/profile.svg";
import setting from "./img/setting.svg";
import logout from "./img/logout.png";

const SideNavbar = () => {
  const [isExpended, setExpendedState] = useState(false);

  const menuItems = [
    {
      text: "Dashboard",
      icon: dash,
    },
    {
      text: "Wallet",
      icon: wallet,
    },
    {
      text: "Exchange Rate",
      icon: exchange,
    },
    {
      text: "Learn",
      icon: learn,
    },
    {
      text: "Blog",
      icon: blog,
    },
    {
      text: "Profile",
      icon: profile,
    },
    {
      text: "Settings",
      icon: setting,
    },
    {
      text: "Logout",
      icon: logout,
    },
  ];

  const trad = [
    {
      first: "demi",
      last: "lade",
    },
    {
      first: "timi",
      last: "lehin",
    },
  ];
  console.log(menuItems);

  return (
    <div
      className={
        isExpended
          ? "side-nav-container"
          : "side-nav-container side-nav-container-NX"
      }
    >
      <div className="nav-upper">
        <div className="nav-heading">
          {isExpended && (
            <div className="nav-brand">
              <img src={bank} alt="" />
              <h2>TradExpress</h2>
            </div>
          )}
          <button
            className={
              isExpended ? "hamburger hamburger-in" : "hamburger hamburger-out"
            }
            onClick={() => setExpendedState(!isExpended)}
          >
            <span></span>
            <span></span>
            <span></span>
          </button>
        </div>
        {menuItems.map((menu) => {
          const { text, icon } = menu;
          return (
            <a
              href="#"
              className={isExpended ? "menu-item " : "menu-item menu-item-NX"}
            >
              <img src={icon} alt="" />
              {isExpended && <p>{text}</p>}
              {<div className="tooltip">{text}</div>}
            </a>
          );
        })}
      </div>
    </div>
  );
};

export default SideNavbar;
